var rides = require('config/ridemodel');
var crypto = require('crypto');
var users = require('config/models')

exports.addride = function(admin, email, token,time,origin,destination,date,millis,freeSpace,totalseats,only_girls,price,transport_mode,transport_mode_info,callback) {

//Creates a new ride with all the parameters send by the user .
			var newride = new rides({
				
			  	admin_name:admin,
			  	admin_email:email,
				// ridecode:"hef874vurkdbc212bhjh2h2h",
				time:time,
				origin:origin,
				date:date,
				destination:destination,
				millis:millis,
				freeSpace:freeSpace,
				totalseats:totalseats,
				only_girls:only_girls,
				price:price,
				transport_mode:transport_mode,
				transport_mode_info:transport_mode_info,
				riders:[] 
			   });

			    newride.save(function (err) {
			    	console.log(err);
			    callback({'response':"Ride Sucessfully Registered"});
			 
			});
};
 
   
