var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var rideSchema = mongoose.Schema({
    adminemail:String,
    ridecode:String,
    date:String,
    starttime:String,
    millis:Number,
    origin:String,
    end:String,
    availableseats:Number,
    totalseats:Number,
    onlygirls:Boolean,
    price:Number,
    mode:String,
    modeinfo:String,
    riders:Array,
});

module.exports = mongoose.model('rides', rideSchema);