var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var rideSchema = mongoose.Schema({

    admin_name:String,
    admin_email:String,
    // ridecode:String,
    date:String,
    time:String,
    millis:Number,
    origin:String,
    destination:String,
    has_booked:Boolean,
    freeSpace:Number,
    // total_seats:Number,
    only_girls:Boolean,
    price:Number,
    transport_mode:String,
    transport_mode_info:String,
    riders:Array,
});

module.exports = mongoose.model('rides', rideSchema);